import React, { useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, Dimensions } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

const TriggeredEffect = ({ isTriggered, onAnimationComplete }) => {
  const animValue = useRef(new Animated.Value(0)).current;
  const waveAnim = useRef(new Animated.Value(0)).current;
  const screenWidth = Dimensions.get('window').width;
  const screenHeight = Dimensions.get('window').height;

  // Create multiple wave points
  const waves = Array(3).fill(0).map(() => ({
    x: Math.random() * screenWidth,
    y: Math.random() * screenHeight,
    delay: Math.random() * 200,
    scale: 0.8 + Math.random() * 0.4,
  }));

  useEffect(() => {
    if (isTriggered) {
      animValue.setValue(0);
      waveAnim.setValue(0);

      Animated.parallel([
        // Opacity animation
        Animated.sequence([
          Animated.timing(animValue, {
            toValue: 0.4,
            duration: 100,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0.2,
            duration: 200,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0,
            duration: 500,
            useNativeDriver: true,
          }),
        ]),
        // Wave expansion
        Animated.timing(waveAnim, {
          toValue: 1,
          duration: 800,
          useNativeDriver: true,
        }),
      ]).start(() => onAnimationComplete?.());
    }
  }, [isTriggered]);

  const renderWave = (config, index) => {
    return (
      <Animated.View
        key={index}
        style={[
          styles.wave,
          {
            left: config.x,
            top: config.y,
            opacity: animValue,
            transform: [
              {
                scale: waveAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [config.scale, config.scale * 4],
                }),
              },
            ],
          },
        ]}
      >
        <LinearGradient
          colors={[
            'rgba(147, 112, 219, 0.15)',
            'rgba(88, 101, 242, 0.25)',
            'rgba(71, 87, 234, 0.08)',
          ]}
          style={styles.waveGradient}
        >
          <View style={[
            styles.waveRipple,
            { borderColor: 'rgba(147, 112, 219, 0.2)' }
          ]} />
        </LinearGradient>
      </Animated.View>
    );
  };

  return (
    <View style={styles.container}>
      {waves.map((wave, index) => renderWave(wave, index))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    ...StyleSheet.absoluteFillObject,
    pointerEvents: 'none',
  },
  wave: {
    position: 'absolute',
    width: 200,
    height: 200,
    borderRadius: 100,
    overflow: 'hidden',
  },
  waveGradient: {
    width: '100%',
    height: '100%',
    borderRadius: 100,
    justifyContent: 'center',
    alignItems: 'center',
  },
  waveRipple: {
    width: '90%',
    height: '90%',
    borderRadius: 90,
    borderWidth: 1,
    borderColor: 'rgba(147, 112, 219, 0.2)',
    backgroundColor: 'transparent',
  },
});

export default TriggeredEffect;
