import React from 'react';
import {
  SafeAreaView,
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
} from 'react-native';
import { 
  useFonts,
  Orbitron_400Regular,
  Orbitron_500Medium,
  Orbitron_600SemiBold,
  Orbitron_700Bold,
} from '@expo-google-fonts/orbitron';

const App = () => {
  let [fontsLoaded] = useFonts({
    Orbitron_400Regular,
    Orbitron_500Medium,
    Orbitron_600SemiBold,
    Orbitron_700Bold,
  });

  if (!fontsLoaded) {
    return null;
  }

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.logo}>EVE</Text>
        <View style={styles.headerRight}>
          <Text style={styles.balance}>EVE: $0.0150</Text>
          <TouchableOpacity style={styles.menuButton}>
            <Text style={styles.menuText}>Menu</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Main Content - Now Centered */}
      <View style={styles.mainContainer}>
        <View style={styles.contentContainer}>
          <Text style={styles.title}>Meet EVE</Text>
          <View style={styles.subtitleContainer}>
            <Text style={styles.subtitle}>your crypto-native</Text>
            <Text style={styles.subtitle}>Personal Agent.</Text>
          </View>

          {/* Whitelist Section */}
          <View style={styles.whitelistSection}>
            <Text style={styles.whitelistTitle}>Join the whitelist</Text>
            <TextInput
              style={styles.input}
              placeholder="Telegram handle"
              placeholderTextColor="#666"
            />
          </View>
        </View>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#0A0A0A',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 20,
    paddingHorizontal: 40,
    borderBottomWidth: 1,
    borderBottomColor: '#333',
  },
  mainContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  contentContainer: {
    width: '100%',
    maxWidth: 800,
    paddingHorizontal: 40,
    alignItems: 'center',
  },
  logo: {
    fontSize: 24,
    color: '#fff',
    fontWeight: '200',
    letterSpacing: 2,
    fontFamily: 'Orbitron_400Regular',
  },
  headerRight: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 20,
  },
  balance: {
    color: '#B8860B',
    backgroundColor: '#1A1A1A',
    padding: 8,
    borderRadius: 4,
    fontSize: 14,
    letterSpacing: 0.5,
    fontFamily: 'Orbitron_400Regular',
  },
  menuButton: {
    borderWidth: 1,
    borderColor: '#B8860B',
    padding: 8,
    borderRadius: 4,
  },
  menuText: {
    color: '#B8860B',
    fontSize: 14,
    letterSpacing: 0.5,
    fontFamily: 'Orbitron_400Regular',
  },
  title: {
    fontSize: 64,
    color: '#fff',
    marginBottom: 20,
    fontWeight: '200',
    letterSpacing: 2,
    fontFamily: 'Orbitron_400Regular',
    textAlign: 'center',
  },
  subtitleContainer: {
    marginBottom: 60,
    alignItems: 'center',
  },
  subtitle: {
    fontSize: 48,
    color: '#fff',
    lineHeight: 56,
    fontWeight: '200',
    letterSpacing: 1,
    fontFamily: 'Orbitron_400Regular',
    textAlign: 'center',
  },
  whitelistSection: {
    width: '100%',
    maxWidth: 400,
    marginTop: 60,
    alignItems: 'center',
  },
  whitelistTitle: {
    color: '#fff',
    fontSize: 32,
    marginBottom: 20,
    fontWeight: '200',
    letterSpacing: 1,
    fontFamily: 'Orbitron_400Regular',
    textAlign: 'center',
  },
  input: {
    borderWidth: 1,
    borderColor: '#B8860B',
    color: '#fff',
    padding: 15,
    borderRadius: 4,
    width: '100%',
    backgroundColor: 'transparent',
    fontSize: 16,
    letterSpacing: 0.5,
    textAlign: 'center',
  },
});

export default App;
