import React, { useEffect, useRef } from 'react';
import Svg, { Line, Defs, Pattern, Rect, LinearGradient, Stop } from 'react-native-svg';
import { View, StyleSheet, Animated } from 'react-native';

const GridBackground = () => {
  const fadeAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    const pulse = Animated.sequence([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 2000,
        useNativeDriver: true,
      }),
      Animated.timing(fadeAnim, {
        toValue: 0,
        duration: 2000,
        useNativeDriver: true,
      })
    ]);

    Animated.loop(pulse).start();
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View style={[styles.gridContainer, { opacity: fadeAnim }]}>
        <Svg height="100%" width="100%" style={styles.grid}>
          <Defs>
            <LinearGradient id="lineGradient" x1="0" y1="0" x2="1" y2="1">
              <Stop offset="0" stopColor="#400000" stopOpacity="0.2" />
              <Stop offset="0.5" stopColor="#600000" stopOpacity="0.4" />
              <Stop offset="1" stopColor="#400000" stopOpacity="0.2" />
            </LinearGradient>
            <Pattern 
              id="grid" 
              width="100" 
              height="100" 
              patternUnits="userSpaceOnUse"
              patternTransform="rotate(45)"
            >
              <Line
                x1="0"
                y1="100"
                x2="100"
                y2="100"
                stroke="url(#lineGradient)"
                strokeWidth="0.5"
              />
              <Line
                x1="100"
                y1="0"
                x2="100"
                y2="100"
                stroke="url(#lineGradient)"
                strokeWidth="0.5"
              />
            </Pattern>
          </Defs>
          <Rect 
            width="100%" 
            height="100%" 
            fill="url(#grid)"
          />
        </Svg>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -1,
  },
  gridContainer: {
    width: '100%',
    height: '100%',
  },
  grid: {
    position: 'absolute',
    top: 0,
    left: 0,
  },
});

export default GridBackground;
