import React, { useEffect, useRef } from 'react';
import Svg, { Line, Defs, Pattern, Rect } from 'react-native-svg';
import { View, StyleSheet, Animated, Easing } from 'react-native';

const GridBackground = () => {
  const fadeAnim = useRef(new Animated.Value(0.5)).current;
  const colorAnim = useRef(new Animated.Value(0)).current;
  const translateX1 = useRef(new Animated.Value(0)).current;
  const translateX2 = useRef(new Animated.Value(0)).current;
  const translateY1 = useRef(new Animated.Value(0)).current;
  const translateY2 = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Breathing effect
    Animated.loop(
      Animated.sequence([
        Animated.timing(fadeAnim, {
          toValue: 0.8,
          duration: 2000,
          easing: Easing.inOut(Easing.sine),
          useNativeDriver: true,
        }),
        Animated.timing(fadeAnim, {
          toValue: 0.5,
          duration: 2000,
          easing: Easing.inOut(Easing.sine),
          useNativeDriver: true,
        })
      ])
    ).start();

    // Color vibration
    Animated.loop(
      Animated.sequence([
        Animated.timing(colorAnim, {
          toValue: 1,
          duration: 1500,
          easing: Easing.inOut(Easing.sine),
          useNativeDriver: false,
        }),
        Animated.timing(colorAnim, {
          toValue: 0,
          duration: 1500,
          easing: Easing.inOut(Easing.sine),
          useNativeDriver: false,
        })
      ])
    ).start();

    // Movement animations
    const startMovement = (anim, toValue, duration) => {
      Animated.loop(
        Animated.sequence([
          Animated.timing(anim, {
            toValue,
            duration,
            easing: Easing.linear,
            useNativeDriver: true,
          }),
          Animated.timing(anim, {
            toValue: 0,
            duration: 0,
            useNativeDriver: true,
          })
        ])
      ).start();
    };

    startMovement(translateX1, -200, 12000);  // Faster
    startMovement(translateX2, 200, 15000);
    startMovement(translateY1, -200, 18000);
    startMovement(translateY2, 200, 20000);
  }, []);

  return (
    <View style={styles.container}>
      {/* Base layer */}
      <Animated.View 
        style={[
          styles.overlay, 
          { 
            opacity: fadeAnim,
            transform: [
              { translateX: translateX1 },
              { translateY: translateY1 }
            ]
          }
        ]}
      >
        <Svg height="200%" width="200%" style={styles.grid}>
          <Defs>
            <Pattern 
              id="grid3" 
              width="200" 
              height="200" 
              patternUnits="userSpaceOnUse"
              patternTransform="rotate(15)"
            >
              <Line
                x1="0"
                y1="200"
                x2="200"
                y2="200"
                stroke="#F4E409"
                strokeWidth="1.2"
                opacity="0.6"
              />
              <Line
                x1="200"
                y1="0"
                x2="200"
                y2="200"
                stroke="#F4E409"
                strokeWidth="1.2"
                opacity="0.6"
              />
            </Pattern>
          </Defs>
          <Rect width="200%" height="200%" fill="url(#grid3)" x="-50%" y="-50%" />
        </Svg>
      </Animated.View>

      {/* Additional layers with similar pattern but different colors and sizes */}
      {/* ... middle and top layers with similar structure but different parameters ... */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -1,
    overflow: 'hidden',
  },
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
  },
  grid: {
    position: 'absolute',
    top: 0,
    left: 0,
  },
});

export default GridBackground;
