import React, { useState, useEffect, useRef } from 'react';
import {
  SafeAreaView,
  StyleSheet,
  View,
  Text,
  Pressable,
  TextInput,
  useWindowDimensions,
  Platform,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useFonts, Orbitron_400Regular, Orbitron_700Bold } from '@expo-google-fonts/orbitron';
import OctahedronSvg from './components/OctahedronSvg';
import AnimatedLines from './components/AnimatedLines';
import DataStream from './components/DataStream';
import TriggeredEffect from './components/TriggeredEffect';
import Terminal from './components/Terminal';

const App = () => {
  const { width } = useWindowDimensions();
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  let [fontsLoaded] = useFonts({
    Orbitron_400Regular,
    Orbitron_700Bold,
  });

  // Add state for octahedron visibility
  const [showOctahedron, setShowOctahedron] = useState(true);

  // Update the resize handler to check terminal collision
  useEffect(() => {
    const handleResize = () => {
      const width = window.innerWidth;
      setIsSmallScreen(width < 768);
      // Hide octahedron on smaller screens or when it would overlap terminal
      setShowOctahedron(width > 1200);
    };

    handleResize(); // Initial check
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const useNativeDriver = Platform.OS !== 'web';

  // Add state for trigger
  const [isEffectTriggered, setIsEffectTriggered] = useState(false);

  // Add trigger handler
  const handleTrigger = () => {
    setIsEffectTriggered(true);
  };

  // Add reset handler
  const handleEffectComplete = () => {
    setIsEffectTriggered(false);
  };

  const dynamicStyles = {
    mainTitle: {
      fontSize: width <= 480 ? 28 : width <= 768 ? 36 : width <= 1024 ? 48 : 64,
      lineHeight: width <= 480 ? 32 : width <= 768 ? 40 : width <= 1024 ? 52 : 68,
      whiteSpace: 'nowrap',
      overflow: 'hidden',
      textOverflow: 'ellipsis',
      width: '100%',
    },
    heroSection: {
      paddingHorizontal: width <= 768 ? 20 : 40,
      paddingTop: width <= 768 ? 40 : 60,
      paddingBottom: width <= 768 ? 40 : 60,
    },
    marketInfo: {
      flexDirection: width <= 768 ? 'column' : 'row',
      gap: width <= 768 ? 15 : 30,
    },
    footerContent: {
      paddingHorizontal: width <= 768 ? 20 : 40,
      paddingVertical: width <= 768 ? 30 : 40,
    },
    systemText: {
      fontSize: width <= 480 ? 12 : 16,
      marginBottom: width <= 480 ? 10 : 15,
    },
    versionText: {
      fontSize: width <= 480 ? 12 : 16,
      marginBottom: width <= 480 ? 20 : 40,
    }
  };

  if (!fontsLoaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      {/* Dark gradient background with noise texture */}
      <LinearGradient
        colors={['#000000', '#0A0A0A', '#060714', '#000000']}
        style={styles.background}
      />

      {/* Animated grid overlay */}
      <View style={styles.gridOverlay} />

      {/* Add the animated lines */}
      <AnimatedLines />

      {/* Multiple decorative octahedrons */}
      {showOctahedron && (
        <View style={styles.decorativeShapes}>
          <View style={styles.octahedronMain}>
            <OctahedronSvg width={700} height={700} />
          </View>
        </View>
      )}

      <DataStream />

      {/* Main content */}
      <SafeAreaView style={styles.content}>
        {/* Navbar */}
        <View style={styles.navbar}>
          <View style={styles.logoContainer}>
            <Text style={styles.logo}>
              <Text style={styles.logoE}>E</Text>
              <Text style={styles.logoVE}>VE</Text>
              <Text style={styles.logoVersion}>v1</Text>
            </Text>
          </View>
          <View style={styles.logoUnderline} />
          <View style={styles.navRight}>
            <Text style={styles.price}>$0.0150</Text>
            <Pressable style={styles.docsButton}>
              <View style={styles.docsButtonGlow} />
              <Text style={styles.docsButtonText}>
                <Text style={styles.docsPrefix}>{'//'}</Text> DOCS
              </Text>
              <View style={styles.docsButtonBorder} />
            </Pressable>
            <Pressable style={styles.connectButton}>
              <Text style={styles.connectButtonText}>CONNECT</Text>
            </Pressable>
          </View>
        </View>

        {/* Hero Section */}
        <View style={[styles.heroSection, dynamicStyles.heroSection]}>
          <View style={styles.heroContent}>
            <Text style={[styles.systemText, dynamicStyles.systemText]}>
              SYSTEM://INITIALIZED
            </Text>
            <Text style={[styles.mainTitle, dynamicStyles.mainTitle]}>
              AUTONOMOUS
            </Text>
            <Text style={[styles.mainTitle, dynamicStyles.mainTitle]}>
              INTELLIGENCE
            </Text>
            <Text style={[styles.versionText, dynamicStyles.versionText]}>
              V.1.0.0
            </Text>
            <Terminal />
          </View>
        </View>

        {/* Footer */}
        <View style={[styles.footer, dynamicStyles.footer]}>
          <LinearGradient
            colors={['transparent', 'rgba(0, 0, 0, 0.95)']}
            style={styles.footerGradient}
          />
          <View style={[styles.footerContent, dynamicStyles.footerContent]}>
            <View style={[styles.marketInfo, dynamicStyles.marketInfo]}>
              <View style={styles.marketItem}>
                <Text style={styles.marketLabel}>CURRENT PRICE:</Text>
                <Text style={styles.marketValue}>$0.03472</Text>
                <Text style={styles.marketChange}>-24.69%</Text>
              </View>
              <Text style={styles.marketDivider}>|</Text>
              <View style={styles.marketItem}>
                <Text style={styles.marketLabel}>MARKET CAP:</Text>
                <Text style={styles.marketValue}>$34,484,366.00</Text>
              </View>
              <Text style={styles.marketDivider}>|</Text>
              <View style={styles.marketItem}>
                <Text style={styles.marketLabel}>HOLDERS:</Text>
                <Text style={styles.marketValue}>87,443</Text>
              </View>
              <Text style={styles.marketDivider}>|</Text>
              <View style={styles.marketItem}>
                <Text style={styles.marketLabel}>24H VOLUME:</Text>
                <Text style={styles.marketValue}>$1,188,114.03</Text>
              </View>
            </View>
            <View style={styles.socialLinks}>
              <Pressable style={styles.socialLink}>
                <Text style={styles.socialLinkText}>
                  <Text style={styles.socialPrefix}>{'>'}  </Text>
                  DEXSCREENER
                </Text>
                <View style={styles.socialLinkHover} />
              </Pressable>

              <Text style={styles.socialDivider}>|</Text>

              <Pressable style={styles.socialLink}>
                <Text style={styles.socialLinkText}>
                  <Text style={styles.socialPrefix}>{'>'}  </Text>
                  X
                </Text>
                <View style={styles.socialLinkHover} />
              </Pressable>

              <Text style={styles.socialDivider}>|</Text>

              <Pressable style={styles.socialLink}>
                <Text style={styles.socialLinkText}>
                  <Text style={styles.socialPrefix}>{'>'}  </Text>
                  VIRTUALS
                </Text>
                <View style={styles.socialLinkHover} />
              </Pressable>
            </View>
          </View>
        </View>
      </SafeAreaView>

      {/* Add triggered effect layer */}
      <TriggeredEffect 
        isTriggered={isEffectTriggered}
        onComplete={() => setIsEffectTriggered(false)}
      />
    </View>
  );
};

