import React, { useEffect, useRef } from 'react';
import { View, Animated, StyleSheet, Dimensions } from 'react-native';

const AnimatedLines = () => {
  const screenWidth = Dimensions.get('window').width;
  const line1 = useRef(new Animated.Value(getRandomStart())).current;
  const line2 = useRef(new Animated.Value(getRandomStart())).current;
  const line3 = useRef(new Animated.Value(getRandomStart())).current;

  // Function to get random start position
  function getRandomStart() {
    return Math.floor(Math.random() * -600) - 200; // Random value between -800 and -200
  }

  // Function to get random duration
  function getRandomDuration() {
    return Math.floor(Math.random() * 1000) + 4400; // Random duration between 4400-5400ms (10% slower than 4000ms)
  }

  const animateLine = (lineAnim) => {
    const randomStart = getRandomStart();
    const randomDuration = getRandomDuration();

    Animated.loop(
      Animated.sequence([
        Animated.timing(lineAnim, {
          toValue: screenWidth + 400,
          duration: randomDuration,
          delay: Math.random() * 2000, // Random delay between 0-2000ms
          useNativeDriver: true,
        }),
        Animated.timing(lineAnim, {
          toValue: randomStart,
          duration: 0,
          useNativeDriver: true,
        })
      ])
    ).start();
  };

  useEffect(() => {
    // Start animations with random timing
    animateLine(line1);
    animateLine(line2);
    animateLine(line3);

    // Restart animations periodically with new random values
    const interval = setInterval(() => {
      animateLine(line1);
      animateLine(line2);
      animateLine(line3);
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line1 }],
            top: '25%',
          }
        ]}
      />
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line2 }],
            top: '45%',
          }
        ]}
      />
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line3 }],
            top: '65%',
          }
        ]}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
  },
  line: {
    position: 'absolute',
    width: 200,
    height: 4,
    backgroundColor: 'transparent',
    borderWidth: 0,
    borderTopWidth: 4,
    borderStyle: 'dotted',
    borderColor: '#F4E409',
    opacity: 0.3,
    borderRadius: 2,
    marginLeft: 20,
    marginRight: 20,
  },
});

export default AnimatedLines;
