import React, { useEffect, useRef } from 'react';
import { View, Animated, StyleSheet, Dimensions } from 'react-native';

const AnimatedLines = () => {
  const screenWidth = Dimensions.get('window').width;
  const line1 = useRef(new Animated.Value(-400)).current;
  const line2 = useRef(new Animated.Value(-400)).current;
  const line3 = useRef(new Animated.Value(-400)).current;

  const animateLine = (lineAnim, delay = 0) => {
    Animated.loop(
      Animated.sequence([
        Animated.timing(lineAnim, {
          toValue: screenWidth + 400,
          duration: 4400, // 10% slower than original
          delay,
          useNativeDriver: true,
        }),
        Animated.timing(lineAnim, {
          toValue: -400,
          duration: 0,
          useNativeDriver: true,
        })
      ])
    ).start();
  };

  useEffect(() => {
    animateLine(line1, 0);
    animateLine(line2, 1500);
    animateLine(line3, 3000);
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line1 }],
            top: '25%',
          }
        ]}
      />
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line2 }],
            top: '45%',
          }
        ]}
      />
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line3 }],
            top: '65%',
          }
        ]}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
  },
  line: {
    position: 'absolute',
    width: 200,
    height: 4,
    backgroundColor: 'transparent',
    borderWidth: 0,
    borderTopWidth: 4,
    borderStyle: 'dotted',
    borderColor: '#F4E409',
    opacity: 0.3,
    borderRadius: 2,
    marginLeft: 20,
    marginRight: 20,
  },
});

export default AnimatedLines;
