import React, { useEffect, useRef } from 'react';
import { View, Animated, StyleSheet, Dimensions } from 'react-native';

const AnimatedLines = () => {
  const screenWidth = Dimensions.get('window').width;
  const line1 = useRef(new Animated.Value(-200)).current;
  const line2 = useRef(new Animated.Value(-400)).current;
  const line3 = useRef(new Animated.Value(-100)).current;

  const animateLine = (lineAnim, delay = 0) => {
    Animated.loop(
      Animated.sequence([
        Animated.timing(lineAnim, {
          toValue: screenWidth + 200,
          duration: 3000,
          delay,
          useNativeDriver: true,
        }),
        Animated.timing(lineAnim, {
          toValue: -200,
          duration: 0,
          useNativeDriver: true,
        })
      ])
    ).start();
  };

  useEffect(() => {
    animateLine(line1);
    animateLine(line2, 1000);
    animateLine(line3, 2000);
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line1 }],
            top: '20%',
          }
        ]}
      />
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line2 }],
            top: '50%',
          }
        ]}
      />
      <Animated.View
        style={[
          styles.line,
          {
            transform: [{ translateX: line3 }],
            top: '80%',
          }
        ]}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
  },
  line: {
    position: 'absolute',
    width: 100,
    height: 2,
    backgroundColor: 'transparent',
    borderWidth: 0,
    borderTopWidth: 2,
    borderStyle: 'dashed',
    borderColor: '#F4E409',
    opacity: 0.3,
  },
});

export default AnimatedLines;
