import React, { useRef, useEffect } from 'react';
import { Animated, StyleSheet, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

const TriggeredEffect = ({ isTriggered, onAnimationComplete }) => {
  const animValue = useRef(new Animated.Value(0)).current;
  const flashPosition = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    if (isTriggered) {
      // Reset animation values
      animValue.setValue(0);
      flashPosition.setValue(0);

      // Create multiple flash sequence
      Animated.parallel([
        // Main flash animation
        Animated.sequence([
          // Quick bright flash
          Animated.timing(animValue, {
            toValue: 0.8,
            duration: 100,
            useNativeDriver: true,
          }),
          // Slight dim
          Animated.timing(animValue, {
            toValue: 0.4,
            duration: 100,
            useNativeDriver: true,
          }),
          // Second flash
          Animated.timing(animValue, {
            toValue: 0.9,
            duration: 80,
            useNativeDriver: true,
          }),
          // Fade out
          Animated.timing(animValue, {
            toValue: 0,
            duration: 300,
            useNativeDriver: true,
          }),
        ]),
        // Flash position movement
        Animated.timing(flashPosition, {
          toValue: 1,
          duration: 580, // Match total duration of flash sequence
          useNativeDriver: true,
        })
      ]).start(() => {
        if (onAnimationComplete) {
          onAnimationComplete();
        }
      });
    }
  }, [isTriggered]);

  return (
    <View style={styles.container}>
      {/* Multiple flash points */}
      <Animated.View style={[
        styles.flashPoint,
        {
          opacity: animValue,
          transform: [
            {
              translateX: flashPosition.interpolate({
                inputRange: [0, 1],
                outputRange: [-100, 100]
              })
            },
            {
              translateY: flashPosition.interpolate({
                inputRange: [0, 0.5, 1],
                outputRange: [-50, 50, -50]
              })
            },
            {
              scale: animValue.interpolate({
                inputRange: [0, 0.5, 1],
                outputRange: [1, 1.5, 1]
              })
            }
          ]
        }
      ]}>
        <LinearGradient
          colors={[
            'rgba(147, 112, 219, 0.4)',
            'rgba(88, 101, 242, 0.6)',
            'rgba(71, 87, 234, 0.2)'
          ]}
          style={styles.flashGradient}
        />
      </Animated.View>

      {/* Secondary flash points with different timings */}
      <Animated.View style={[
        styles.flashPoint,
        styles.flashPoint2,
        {
          opacity: animValue.interpolate({
            inputRange: [0, 0.5, 1],
            outputRange: [0, 0.7, 0]
          }),
          transform: [
            {
              translateX: flashPosition.interpolate({
                inputRange: [0, 1],
                outputRange: [100, -100]
              })
            },
            {
              scale: animValue.interpolate({
                inputRange: [0, 0.5, 1],
                outputRange: [0.5, 2, 0.5]
              })
            }
          ]
        }
      ]}>
        <LinearGradient
          colors={[
            'rgba(138, 43, 226, 0.3)',
            'rgba(72, 61, 139, 0.5)',
            'rgba(25, 25, 112, 0.2)'
          ]}
          style={styles.flashGradient}
        />
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    ...StyleSheet.absoluteFillObject,
    pointerEvents: 'none',
    overflow: 'hidden',
  },
  flashPoint: {
    position: 'absolute',
    width: 200,
    height: 200,
    borderRadius: 100,
    top: '30%',
    left: '40%',
  },
  flashPoint2: {
    width: 300,
    height: 300,
    top: '50%',
    left: '30%',
  },
  flashGradient: {
    width: '100%',
    height: '100%',
    borderRadius: 100,
    filter: 'blur(20px)',
  },
});

export default TriggeredEffect;
