import React, { useEffect, useRef } from 'react';
import Svg, { Line, Defs, Pattern, Rect } from 'react-native-svg';
import { View, StyleSheet, Animated } from 'react-native';

const GridBackground = () => {
  const fadeAnim = useRef(new Animated.Value(0.3)).current;
  const translateAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Breathing animation
    const breathingAnimation = Animated.sequence([
      Animated.timing(fadeAnim, {
        toValue: 0.6,
        duration: 2000,
        useNativeDriver: true,
      }),
      Animated.timing(fadeAnim, {
        toValue: 0.3,
        duration: 2000,
        useNativeDriver: true,
      })
    ]);

    // Movement animation
    const movementAnimation = Animated.sequence([
      Animated.timing(translateAnim, {
        toValue: 50,
        duration: 3000,
        useNativeDriver: true,
      }),
      Animated.timing(translateAnim, {
        toValue: 0,
        duration: 0,
        useNativeDriver: true,
      })
    ]);

    // Start both animations in loop
    Animated.loop(breathingAnimation).start();
    Animated.loop(movementAnimation).start();
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View 
        style={[
          styles.overlay, 
          { 
            opacity: fadeAnim,
            transform: [{ translateX: translateAnim }]
          }
        ]}
      >
        <Svg height="100%" width="100%" style={styles.grid}>
          <Defs>
            <Pattern 
              id="grid" 
              width="50" 
              height="50" 
              patternUnits="userSpaceOnUse"
              patternTransform="rotate(45)"
            >
              <Line
                x1="0"
                y1="50"
                x2="50"
                y2="50"
                stroke="#500000"
                strokeWidth="0.5"
              />
              <Line
                x1="50"
                y1="0"
                x2="50"
                y2="50"
                stroke="#500000"
                strokeWidth="0.5"
              />
            </Pattern>
          </Defs>
          <Rect width="100%" height="100%" fill="url(#grid)" />
        </Svg>
      </Animated.View>

      {/* Second grid layer with different timing */}
      <Animated.View 
        style={[
          styles.overlay, 
          { 
            opacity: fadeAnim,
            transform: [{ translateX: Animated.multiply(translateAnim, -1) }]
          }
        ]}
      >
        <Svg height="100%" width="100%" style={styles.grid}>
          <Defs>
            <Pattern 
              id="grid2" 
              width="100" 
              height="100" 
              patternUnits="userSpaceOnUse"
              patternTransform="rotate(-45)"
            >
              <Line
                x1="0"
                y1="100"
                x2="100"
                y2="100"
                stroke="#400000"
                strokeWidth="0.3"
              />
              <Line
                x1="100"
                y1="0"
                x2="100"
                y2="100"
                stroke="#400000"
                strokeWidth="0.3"
              />
            </Pattern>
          </Defs>
          <Rect width="120%" height="120%" fill="url(#grid2)" x="-10%" y="-10%" />
        </Svg>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -1,
    overflow: 'hidden',
  },
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
  },
  grid: {
    position: 'absolute',
    top: 0,
    left: 0,
  },
});

export default GridBackground;
