import React, { useState, useEffect, useRef } from 'react';
import {
  SafeAreaView,
  StyleSheet,
  View,
  Text,
  Pressable,
  TextInput,
  useWindowDimensions,
  Animated,
  Easing,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useFonts, Orbitron_400Regular, Orbitron_700Bold } from '@expo-google-fonts/orbitron';
import OctahedronSvg from './components/OctahedronSvg';
import AnimatedLines from './components/AnimatedLines';
import DataStream from './components/DataStream';

const App = () => {
  const { width: windowWidth } = useWindowDimensions();
  const [isSmallScreen, setIsSmallScreen] = useState(windowWidth < 768);

  let [fontsLoaded] = useFonts({
    Orbitron_400Regular,
    Orbitron_700Bold,
  });

  useEffect(() => {
    setIsSmallScreen(windowWidth < 768);
  }, [windowWidth]);

  // Add new animation value for breathing effect
  const breatheAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Add breathing animation
    Animated.loop(
      Animated.sequence([
        Animated.timing(breatheAnim, {
          toValue: 1,
          duration: 4000,
          easing: Easing.inOut(Easing.sine),
          useNativeDriver: true,
        }),
        Animated.timing(breatheAnim, {
          toValue: 0,
          duration: 4000,
          easing: Easing.inOut(Easing.sine),
          useNativeDriver: true,
        })
      ])
    ).start();
  }, []);

  // Create interpolated values for the breathing effect
  const titleScale = breatheAnim.interpolate({
    inputRange: [0, 1],
    outputRange: [1, 1.04]  // Increased from 1.02 to 1.04 (4% max scale)
  });

  const titleOpacity = breatheAnim.interpolate({
    inputRange: [0, 1],
    outputRange: [0.93, 1]  // Increased opacity range for more visibility
  });

  if (!fontsLoaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      {/* Dark gradient background with noise texture */}
      <LinearGradient
        colors={['#000000', '#0A0A0A', '#060714', '#000000']}
        style={styles.background}
      />

      {/* Animated grid overlay */}
      <View style={styles.gridOverlay} />

      {/* Add the animated lines */}
      <AnimatedLines />

      {/* Multiple decorative octahedrons */}
      <View style={styles.decorativeShapes}>
        {/* Main large octahedron */}
        <View style={styles.octahedronMain}>
          <OctahedronSvg width={500} height={500} />
        </View>
      </View>

      <DataStream />

      {/* Main content */}
      <SafeAreaView style={styles.content}>
        {/* Navbar */}
        <View style={styles.navbar}>
          <View style={styles.logoContainer}>
            <View style={styles.logoGlow} />
            <Text style={styles.logo}>
              <Text style={styles.logoHex}>0x</Text>
              <Text style={styles.logoE}>E</Text>
              <Text style={styles.logoVE}>VE</Text>
            </Text>
            <View style={styles.logoAccent1} />
            <View style={styles.logoAccent2} />
          </View>
          <View style={styles.logoUnderline} />
          <View style={styles.navRight}>
            <Text style={styles.price}>$0.0150</Text>
            <Pressable style={styles.docsButton}>
              <View style={styles.docsButtonGlow} />
              <Text style={styles.docsButtonText}>
                <Text style={styles.docsPrefix}>{'//'}</Text> DOCS
              </Text>
              <View style={styles.docsButtonBorder} />
            </Pressable>
            <Pressable style={styles.connectButton}>
              <Text style={styles.connectButtonText}>CONNECT</Text>
            </Pressable>
          </View>
        </View>

        {/* Hero Section */}
        <View style={styles.heroSection}>
          <View style={styles.heroContent}>
            <Text style={styles.systemText}>SYSTEM://INITIALIZED</Text>
            <Animated.Text style={[
              styles.mainTitle,
              {
                transform: [{ scale: titleScale }],
                opacity: titleOpacity,
              }
            ]}>
              AUTONOMOUS
            </Animated.Text>
            <Animated.Text style={[
              styles.mainTitle,
              {
                transform: [{ scale: titleScale }],
                opacity: titleOpacity,
              }
            ]}>
              INTELLIGENCE
            </Animated.Text>
            <Text style={styles.versionText}>V.1.0.0</Text>

            {/* Terminal Window */}
            <View style={styles.terminalWindow}>
              <View style={styles.terminalHeader}>
                <Text style={styles.terminalTitle}>> Your Personal Assistant</Text>
                <View style={styles.terminalControls}>
                  <View style={[styles.terminalDot, { backgroundColor: '#FF5F57' }]} />
                  <View style={[styles.terminalDot, { backgroundColor: '#FFBD2E' }]} />
                  <View style={[styles.terminalDot, { backgroundColor: '#28CA41' }]} />
                </View>
              </View>
              <View style={styles.terminalBody}>
                <View style={styles.terminalLines}>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text>Initializing EVE protocol...</Text>
                  </Text>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text>Connecting to decentralized network...</Text>
                  </Text>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text>Loading AI modules...</Text>
                  </Text>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text style={styles.terminalHighlight}>Ready for interaction.</Text>
                  </Text>
                </View>
                <View style={styles.terminalInputLine}>
                  <Text style={styles.terminalPrompt}>{">_ "}</Text>
                  <TextInput
                    style={styles.terminalInput}
                    placeholder="Enter command..."
                    placeholderTextColor="rgba(244, 228, 9, 0.3)"
                    inputMode="text"
                    readOnly={false}
                  />
                </View>
              </View>
            </View>
          </View>
        </View>

        {/* Footer */}
        <View style={styles.footer}>
          <LinearGradient
            colors={['transparent', 'rgba(244, 228, 9, 0.05)']}
            style={styles.footerGradient}
          />
          <View style={styles.footerContent}>
            <View style={styles.socialLinks}>
              <Pressable style={styles.socialLink}>
                <Text style={styles.socialLinkText}>
                  <Text style={styles.socialPrefix}>> </Text>
                  X_PROTOCOL
                </Text>
                <View style={styles.socialLinkHover} />
              </Pressable>

              <Text style={styles.socialDivider}>|</Text>

              <Pressable style={styles.socialLink}>
                <Text style={styles.socialLinkText}>
                  <Text style={styles.socialPrefix}>> </Text>
                  TG_NETWORK
                </Text>
                <View style={styles.socialLinkHover} />
              </Pressable>

              <Text style={styles.socialDivider}>|</Text>

              <Pressable style={styles.socialLink}>
                <Text style={styles.socialLinkText}>
                  <Text style={styles.socialPrefix}>> </Text>
                  DEX_STATS
                </Text>
                <View style={styles.socialLinkHover} />
              </Pressable>
            </View>
          </View>
        </View>
      </SafeAreaView>
    </View>
  );
};

