import React, { useState, useEffect, useRef } from 'react';
import {
  SafeAreaView,
  StyleSheet,
  View,
  Text,
  Pressable,
  TextInput,
  useWindowDimensions,
  Animated,
  Easing,
  Platform,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useFonts, Orbitron_400Regular, Orbitron_700Bold } from '@expo-google-fonts/orbitron';
import OctahedronSvg from './components/OctahedronSvg';
import AnimatedLines from './components/AnimatedLines';
import DataStream from './components/DataStream';
import TriggeredEffect from './components/TriggeredEffect';

// Move styles that need responsive values outside StyleSheet
const getResponsiveStyles = (isMobileScreen, isSmallScreen) => ({
  navbar: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: Platform.select({
      web: {
        paddingHorizontal: isMobileScreen ? 15 : 20,
        paddingVertical: isMobileScreen ? 10 : 20,
      },
      default: {
        padding: isMobileScreen ? 15 : 20,
      }
    }),
    height: isMobileScreen ? 60 : 80,
    borderBottomWidth: 1,
    borderBottomColor: 'rgba(244, 228, 9, 0.1)',
  },
  mainTitle: {
    fontSize: isMobileScreen ? 36 : isSmallScreen ? 54 : 81,
    lineHeight: isMobileScreen ? 42 : isSmallScreen ? 60 : 90,
    color: '#F4E409',
    fontFamily: 'Orbitron_700Bold',
    letterSpacing: isMobileScreen ? 1 : 2,
    textTransform: 'uppercase',
    textShadow: '0 0 20px rgba(244, 228, 9, 0.5)',
    marginBottom: isMobileScreen ? 10 : 15,
    cursor: 'pointer',
  },
  heroSection: {
    flex: 1,
    paddingTop: isMobileScreen ? 20 : isSmallScreen ? 40 : 60,
    paddingHorizontal: isMobileScreen ? 15 : isSmallScreen ? 30 : 40,
    position: 'relative',
    zIndex: 1,
  },
  heroContent: {
    maxWidth: isMobileScreen ? '100%' : isSmallScreen ? 600 : 1200,
    width: '100%',
    marginLeft: 'auto',
    marginRight: 'auto',
    paddingBottom: isMobileScreen ? 40 : 60,
  },
  terminalWindow: {
    width: '100%',
    maxWidth: isMobileScreen ? '100%' : isSmallScreen ? 600 : 800,
    backgroundColor: 'rgba(0, 0, 0, 0.8)',
    borderRadius: isMobileScreen ? 8 : 10,
    borderWidth: 1,
    borderColor: '#F4E409',
    overflow: 'hidden',
  },
  marketInfo: {
    flexDirection: isMobileScreen ? 'column' : 'row',
    alignItems: isMobileScreen ? 'flex-start' : 'center',
    gap: isMobileScreen ? 15 : 30,
    marginBottom: isMobileScreen ? 20 : 30,
    flexWrap: isSmallScreen ? 'wrap' : 'nowrap',
  },
  socialLinks: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: isMobileScreen ? 15 : 30,
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  footer: {
    position: 'relative',
    height: isMobileScreen ? 'auto' : 180,
    minHeight: isMobileScreen ? 240 : 180,
    width: '100%',
    overflow: 'hidden',
    paddingHorizontal: isMobileScreen ? 15 : 40,
    paddingVertical: isMobileScreen ? 30 : 40,
  },
  navRight: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: isMobileScreen ? 10 : 20,
  },
  logo: {
    fontSize: isMobileScreen ? 28 : isSmallScreen ? 32 : 36,
    fontFamily: 'Orbitron_700Bold',
    letterSpacing: 1,
  },
  systemText: {
    fontSize: isMobileScreen ? 12 : isSmallScreen ? 14 : 16,
    letterSpacing: isMobileScreen ? 2 : 4,
    marginBottom: isMobileScreen ? 10 : 15,
  },
  versionText: {
    fontSize: isMobileScreen ? 12 : isSmallScreen ? 14 : 16,
    letterSpacing: isMobileScreen ? 2 : 4,
    marginBottom: isMobileScreen ? 30 : 40,
  },
});

const App = () => {
  const { width: windowWidth, height: windowHeight } = useWindowDimensions();
  const [isSmallScreen, setIsSmallScreen] = useState(windowWidth < 768);
  const [isMobileScreen, setIsMobileScreen] = useState(windowWidth < 480);

  let [fontsLoaded] = useFonts({
    Orbitron_400Regular,
    Orbitron_700Bold,
  });

  useEffect(() => {
    setIsSmallScreen(windowWidth < 768);
    setIsMobileScreen(windowWidth < 480);
  }, [windowWidth]);

  // Add new animation value for breathing effect
  const breatheAnim = useRef(new Animated.Value(0)).current;
  const logoBreathAnim = useRef(new Animated.Value(0)).current;

  const useNativeDriver = Platform.OS !== 'web';

  useEffect(() => {
    // Create continuous breathing animations
    const breathingAnimation = Animated.loop(
      Animated.sequence([
        Animated.timing(breatheAnim, {
          toValue: 1,
          duration: 4000,
          easing: Easing.linear,
          useNativeDriver: false,
        }),
        Animated.timing(breatheAnim, {
          toValue: 0,
          duration: 4000,
          easing: Easing.linear,
          useNativeDriver: false,
        })
      ])
    );

    const logoBreathingAnimation = Animated.loop(
      Animated.sequence([
        Animated.timing(logoBreathAnim, {
          toValue: 1,
          duration: 3000,
          easing: Easing.linear,
          useNativeDriver: false,
        }),
        Animated.timing(logoBreathAnim, {
          toValue: 0,
          duration: 3000,
          easing: Easing.linear,
          useNativeDriver: false,
        })
      ])
    );

    breathingAnimation.start();
    logoBreathingAnimation.start();

    return () => {
      breathingAnimation.stop();
      logoBreathingAnimation.stop();
    };
  }, []);

  // Create interpolated values for the breathing effect
  const titleScale = breatheAnim.interpolate({
    inputRange: [0, 1],
    outputRange: [1, 1.02],
  });

  const heroScale = breatheAnim.interpolate({
    inputRange: [0, 1],
    outputRange: [1, 1.02],
  });

  const logoScale = logoBreathAnim.interpolate({
    inputRange: [0, 1],
    outputRange: [1, 1.02],
  });

  const titleOpacity = breatheAnim.interpolate({
    inputRange: [0, 1],
    outputRange: [0.85, 1],
  });

  // Add state for trigger
  const [isEffectTriggered, setIsEffectTriggered] = useState(false);

  // Add trigger handler
  const handleTrigger = () => {
    setIsEffectTriggered(true);
  };

  // Add reset handler
  const handleEffectComplete = () => {
    setIsEffectTriggered(false);
  };

  // Get responsive styles inside the component
  const responsiveStyles = getResponsiveStyles(isMobileScreen, isSmallScreen);

  if (!fontsLoaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      {/* Dark gradient background with noise texture */}
      <LinearGradient
        colors={['#000000', '#0A0A0A', '#060714', '#000000']}
        style={styles.background}
      />

      {/* Animated grid overlay */}
      <View style={styles.gridOverlay} />

      {/* Add the animated lines */}
      <AnimatedLines />

      {/* Multiple decorative octahedrons */}
      <View style={styles.decorativeShapes}>
        <View style={styles.octahedronMain}>
          <OctahedronSvg width={700} height={700} />
        </View>
      </View>

      <DataStream />

      {/* Main content */}
      <SafeAreaView style={styles.content}>
        {/* Navbar */}
        <View style={responsiveStyles.navbar}>
          <View style={styles.logoContainer}>
            <Animated.Text style={[
              styles.logo,
              responsiveStyles.logo,
              {
                transform: [{ scale: logoScale }]
              }
            ]}>
              <Text style={styles.logoE}>E</Text>
              <Text style={styles.logoVE}>VE</Text>
              <Text style={styles.logoVersion}>v1</Text>
            </Animated.Text>
          </View>
          <View style={styles.logoUnderline} />
          <View style={responsiveStyles.navRight}>
            <Text style={styles.price}>$0.0150</Text>
            <Pressable style={styles.docsButton}>
              <View style={styles.docsButtonGlow} />
              <Text style={styles.docsButtonText}>
                <Text style={styles.docsPrefix}>{'//'}</Text> DOCS
              </Text>
              <View style={styles.docsButtonBorder} />
            </Pressable>
            <Pressable style={styles.connectButton}>
              <Text style={styles.connectButtonText}>CONNECT</Text>
            </Pressable>
          </View>
        </View>

        {/* Hero Section */}
        <Animated.View style={[
          responsiveStyles.heroSection,
          {
            transform: [{ scale: heroScale }]
          }
        ]}>
          <View style={responsiveStyles.heroContent}>
            <Text style={[styles.systemText, responsiveStyles.systemText]}>SYSTEM://INITIALIZED</Text>
            <Animated.Text 
              style={[
                responsiveStyles.mainTitle,
                {
                  transform: [{ scale: titleScale }],
                }
              ]}
              onPress={handleTrigger}
            >
              AUTONOMOUS
            </Animated.Text>
            <Animated.Text 
              style={[
                responsiveStyles.mainTitle,
                {
                  transform: [{ scale: titleScale }],
                }
              ]}
              onPress={handleTrigger}
            >
              INTELLIGENCE
            </Animated.Text>
            <Text style={[styles.versionText, responsiveStyles.versionText]}>V.1.0.0</Text>

            {/* Terminal Window */}
            <View style={responsiveStyles.terminalWindow}>
              <View style={styles.terminalHeader}>
                <Text style={styles.terminalTitle}>{'>'}  Your Personal Assistant</Text>
                <View style={styles.terminalControls}>
                  <View style={[styles.terminalDot, { backgroundColor: '#FF5F57' }]} />
                  <View style={[styles.terminalDot, { backgroundColor: '#FFBD2E' }]} />
                  <View style={[styles.terminalDot, { backgroundColor: '#28CA41' }]} />
                </View>
              </View>
              <View style={styles.terminalBody}>
                <View style={styles.terminalLines}>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text>Initializing EVE protocol...</Text>
                  </Text>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text>Connecting to neural networks...</Text>
                  </Text>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text>Loading synaps connections...</Text>
                  </Text>
                  <Text style={styles.terminalLine}>
                    <Text style={styles.terminalPrompt}>{"> "}</Text>
                    <Text style={styles.terminalHighlight}>I have become your digital twin.</Text>
                  </Text>
                </View>

                {/* Add command options */}
                <View style={styles.commandOptions}>
                  <Pressable style={styles.commandButton}>
                    <Text style={styles.commandButtonText}>
                      <Text style={styles.commandPrefix}>{"> "}</Text>
                      Who is Eve?
                    </Text>
                  </Pressable>
                  <Pressable style={styles.commandButton}>
                    <Text style={styles.commandButtonText}>
                      <Text style={styles.commandPrefix}>{"> "}</Text>
                      What can Eve do?
                    </Text>
                  </Pressable>
                  <Pressable style={styles.commandButton}>
                    <Text style={styles.commandButtonText}>
                      <Text style={styles.commandPrefix}>{"> "}</Text>
                      How can I gain access to Eve?
                    </Text>
                  </Pressable>
                </View>

                <View style={styles.terminalInputLine}>
                  <Text style={styles.terminalPrompt}>{">_ "}</Text>
                  <TextInput
                    style={styles.terminalInput}
                    placeholder="Enter command..."
                    placeholderTextColor="rgba(244, 228, 9, 0.3)"
                    inputMode="text"
                    readOnly={false}
                    rows={1}
                    aria-label="Terminal input"
                  />
                </View>
              </View>
            </View>
          </View>
        </Animated.View>

        {/* Footer */}
        <View style={[styles.footer, responsiveStyles.footer]}>
          <LinearGradient
            colors={['transparent', 'rgba(0, 0, 0, 0.95)']}
            style={styles.footerGradient}
          />
          <View style={styles.footerContent}>
            <View style={[styles.marketInfo, responsiveStyles.marketInfo]}>
              <View style={styles.marketItem}>
                <Text style={styles.marketLabel}>CURRENT PRICE:</Text>
                <Text style={styles.marketValue}>$0.03472</Text>
                <Text style={styles.marketChange}>-24.69%</Text>
              </View>
              <Text style={styles.marketDivider}>|</Text>
              <View style={styles.marketItem}>
                <Text style={styles.marketLabel}>MARKET CAP:</Text>
                <Text style={styles.marketValue}>$34,484,366.00</Text>
              </View>
