import React, { useEffect, useRef } from 'react';
import { View, Animated, StyleSheet, Dimensions } from 'react-native';

const AnimatedLines = () => {
  const screenWidth = Dimensions.get('window').width;
  const screenHeight = Dimensions.get('window').height;

  // Create multiple line animations
  const createLineConfig = () => {
    const startFromTop = Math.random() > 0.5;
    const willTurn = Math.random() > 0.5;
    const turnDirection = Math.random() > 0.5 ? 1 : -1; // 1 for up, -1 for down
    const turnPoint = Math.random() * (screenWidth * 0.6) + (screenWidth * 0.2); // Turn between 20% and 80% of screen
    const startY = startFromTop ? -100 : Math.random() * screenHeight;
    const distance = willTurn ? screenHeight * 1.5 : screenWidth + 400;

    return {
      startY,
      willTurn,
      turnPoint,
      turnDirection,
      distance,
      delay: Math.random() * 3000,
    };
  };

  const lines = Array(3).fill(0).map(() => {
    const position = useRef(new Animated.Value(-400)).current;
    const verticalPosition = useRef(new Animated.Value(0)).current;
    return { position, verticalPosition, config: createLineConfig() };
  });

  useEffect(() => {
    lines.forEach(line => {
      const { position, verticalPosition, config } = line;

      const animate = () => {
        position.setValue(-400);
        verticalPosition.setValue(0);

        const animations = [
          Animated.timing(position, {
            toValue: config.willTurn ? config.turnPoint : screenWidth + 400,
            duration: 4400,
            useNativeDriver: true,
          })
        ];

        if (config.willTurn) {
          animations.push(
            Animated.timing(verticalPosition, {
              toValue: config.turnDirection * config.distance,
              duration: 4400,
              useNativeDriver: true,
            })
          );
        }

        Animated.sequence([
          Animated.delay(config.delay),
          Animated.parallel(animations)
        ]).start(() => animate());
      };

      animate();
    });
  }, []);

  return (
    <View style={styles.container}>
      {lines.map((line, index) => (
        <Animated.View
          key={index}
          style={[
            styles.line,
            {
              top: line.config.startY,
              transform: [
                { translateX: line.position },
                { translateY: line.verticalPosition }
              ]
            }
          ]}
        />
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
  },
  line: {
    position: 'absolute',
    width: 200,
    height: 4,
    backgroundColor: 'transparent',
    borderWidth: 0,
    borderTopWidth: 4,
    borderStyle: 'dotted',
    borderColor: '#F4E409',
    opacity: 0.3,
    borderRadius: 2,
    marginLeft: 20,
    marginRight: 20,
  },
});

export default AnimatedLines;
