import React, { useEffect, useRef } from 'react';
import { View, Animated, StyleSheet } from 'react-native';

const DataStream = () => {
  const streamAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    Animated.loop(
      Animated.sequence([
        Animated.timing(streamAnim, {
          toValue: 1,
          duration: 15000,
          useNativeDriver: true,
        }),
        Animated.timing(streamAnim, {
          toValue: 0,
          duration: 0,
          useNativeDriver: true,
        })
      ])
    ).start();
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.stream,
          {
            transform: [
              {
                translateY: streamAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [-300, 1000]
                })
              }
            ]
          }
        ]}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
  },
  stream: {
    position: 'absolute',
    width: '100%',
    height: 300,
    backgroundColor: 'transparent',
    backgroundImage: 'linear-gradient(0deg, transparent, rgba(244, 228, 9, 0.05), transparent)',
  },
});

export default DataStream;
