import React, { useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, Dimensions } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

const TriggeredEffect = ({ isTriggered, onAnimationComplete }) => {
  const animValue = useRef(new Animated.Value(0)).current;
  const flashPosition = useRef(new Animated.Value(0)).current;
  const rotateAnim = useRef(new Animated.Value(0)).current;
  const screenWidth = Dimensions.get('window').width;
  const screenHeight = Dimensions.get('window').height;

  const getRandomPosition = () => ({
    x: Math.random() * screenWidth,
    y: Math.random() * screenHeight,
    rotation: Math.random() * 360,
    scale: 0.5 + Math.random() * 1.5,
    shape: Math.floor(Math.random() * 4), // Random shape type
  });

  const positions = Array(5).fill(0).map(() => getRandomPosition());

  useEffect(() => {
    if (isTriggered) {
      animValue.setValue(0);
      flashPosition.setValue(0);
      rotateAnim.setValue(0);

      Animated.parallel([
        Animated.sequence([
          Animated.timing(animValue, {
            toValue: 0.8,
            duration: 30,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0.2,
            duration: 150,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0.6,
            duration: 30,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0,
            duration: 300,
            useNativeDriver: true,
          }),
        ]),
        Animated.timing(rotateAnim, {
          toValue: 1,
          duration: 510,
          useNativeDriver: true,
        }),
        Animated.timing(flashPosition, {
          toValue: 1,
          duration: 510,
          useNativeDriver: true,
        })
      ]).start(() => onAnimationComplete?.());
    }
  }, [isTriggered]);

  const renderFlashShape = (position, index) => {
    const rotate = rotateAnim.interpolate({
      inputRange: [0, 1],
      outputRange: [`${position.rotation}deg`, `${position.rotation + 180}deg`]
    });

    const getShapeStyle = () => {
      switch(position.shape) {
        case 0: // Diamond
          return {
            transform: [{ rotate: '45deg' }],
            borderRadius: 4,
          };
        case 1: // Irregular hexagon
          return {
            clipPath: 'polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%)',
          };
        case 2: // Abstract shape
          return {
            clipPath: 'polygon(30% 0%, 70% 0%, 100% 30%, 100% 70%, 70% 100%, 30% 100%, 0% 70%, 0% 30%)',
          };
        default: // Blob-like shape
          return {
            borderRadius: '60% 40% 30% 70% / 60% 30% 70% 40%',
          };
      }
    };

    return (
      <Animated.View
        key={index}
        style={[
          styles.flashShape,
          getShapeStyle(),
          {
            left: position.x,
            top: position.y,
            opacity: animValue,
            transform: [
              { scale: position.scale },
              { rotate },
              {
                translateX: flashPosition.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0, (Math.random() - 0.5) * 300],
                }),
              },
              {
                translateY: flashPosition.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0, (Math.random() - 0.5) * 300],
                }),
              },
            ],
          },
        ]}
      >
        <LinearGradient
          colors={[
            'rgba(147, 112, 219, 0.2)',
            'rgba(88, 101, 242, 0.4)',
            'rgba(71, 87, 234, 0.1)',
          ]}
          style={[styles.flashGradient, getShapeStyle()]}
        />
        <View style={[styles.innerGlow, getShapeStyle()]} />
      </Animated.View>
    );
  };

  return (
    <View style={styles.container}>
      {positions.map((pos, index) => renderFlashShape(pos, index))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    ...StyleSheet.absoluteFillObject,
    pointerEvents: 'none',
    overflow: 'hidden',
  },
  flashShape: {
    position: 'absolute',
    width: 180,
    height: 180,
    overflow: 'hidden',
  },
  flashGradient: {
    width: '100%',
    height: '100%',
    opacity: 0.6,
  },
  innerGlow: {
    position: 'absolute',
    top: '20%',
    left: '20%',
    width: '60%',
    height: '60%',
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    filter: 'blur(20px)',
  },
});

export default TriggeredEffect;
