import React, { useRef, useEffect } from 'react';
import { Animated, StyleSheet, View, Dimensions } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

const TriggeredEffect = ({ isTriggered, onAnimationComplete }) => {
  const animValue = useRef(new Animated.Value(0)).current;
  const flashPosition = useRef(new Animated.Value(0)).current;
  const screenWidth = Dimensions.get('window').width;
  const screenHeight = Dimensions.get('window').height;

  // Generate random positions for each flash
  const getRandomPosition = () => ({
    x: Math.random() * screenWidth,
    y: Math.random() * screenHeight,
  });

  const positions = [
    getRandomPosition(),
    getRandomPosition(),
    getRandomPosition(),
  ];

  useEffect(() => {
    if (isTriggered) {
      animValue.setValue(0);
      flashPosition.setValue(0);

      Animated.parallel([
        // Main flash sequence
        Animated.sequence([
          Animated.timing(animValue, {
            toValue: 0.9,
            duration: 50,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0.3,
            duration: 100,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0.8,
            duration: 50,
            useNativeDriver: true,
          }),
          Animated.timing(animValue, {
            toValue: 0,
            duration: 400,
            useNativeDriver: true,
          }),
        ]),
        // Position animation
        Animated.timing(flashPosition, {
          toValue: 1,
          duration: 600,
          useNativeDriver: true,
        })
      ]).start(() => onAnimationComplete?.());
    }
  }, [isTriggered]);

  const renderFlashPoint = (position, index) => {
    const delay = index * 100;
    const scale = animValue.interpolate({
      inputRange: [0, 0.5, 1],
      outputRange: [0.8, 2, 0.8],
    });

    return (
      <Animated.View
        key={index}
        style={[
          styles.flashPoint,
          {
            left: position.x,
            top: position.y,
            opacity: animValue,
            transform: [
              { scale },
              {
                translateX: flashPosition.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0, (Math.random() - 0.5) * 200],
                }),
              },
              {
                translateY: flashPosition.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0, (Math.random() - 0.5) * 200],
                }),
              },
            ],
          },
        ]}
      >
        <LinearGradient
          colors={[
            'rgba(147, 112, 219, 0.4)',
            'rgba(88, 101, 242, 0.8)',
            'rgba(71, 87, 234, 0.2)',
          ]}
          style={styles.flashGradient}
        />
        <View style={styles.innerGlow} />
      </Animated.View>
    );
  };

  return (
    <View style={styles.container}>
      {positions.map((pos, index) => renderFlashPoint(pos, index))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    ...StyleSheet.absoluteFillObject,
    pointerEvents: 'none',
    overflow: 'hidden',
  },
  flashPoint: {
    position: 'absolute',
    width: 150,
    height: 150,
    borderRadius: 75,
  },
  flashGradient: {
    width: '100%',
    height: '100%',
    borderRadius: 75,
    opacity: 0.8,
  },
  innerGlow: {
    position: 'absolute',
    top: '25%',
    left: '25%',
    width: '50%',
    height: '50%',
    borderRadius: 37.5,
    backgroundColor: 'rgba(255, 255, 255, 0.4)',
    filter: 'blur(15px)',
  },
});

export default TriggeredEffect;
